import React, { useState, useEffect } from 'react'
import axios from 'axios'

function Postform({ id }) {
    const [name, setName] = useState('');
    const [reating, setReating] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('name', name);
        formData.append('reating', reating);

        const url = 'http://localhost:8000/post/64588d3cb79f7d0033b4f0fd'

        try {
            const response = await fetch('http://localhost:8000/admin/create', {
                method: 'POST',
                body: formData,
              });
        } catch (err) {
            console.log(err)
        }
        
    }

    
    

  return (
    <div>
        <form>
            <input type='text' placeholder='write our comment....' onChange={(e) => setName(e.target.value)} />
            <input type='number' onChange={(e) => setReating(e.target.value)} />
            <button onSubmit={handleSubmit}>post</button>
        </form>
    </div>
  )
}

export default Postform
