import React, { useState } from 'react'
import axios from 'axios'

function Postform({ id }) {
    const [name, setName] = useState('');
    const [reating, setReating] = useState('');

    const ids = id

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`http://localhost:8000/post/${ids}`, {
                name,
                reating,
              })
              .then(function (response) {
                console.log(response);
              })
              .catch(function (error) {
                console.log(error);
              });
        } catch (err) {
            console.log(err)
        }
        
    }

  return (
    <div>
        <form>
            <textarea placeholder='write our comment....' onChange={(e) => setName(e.target.value)}></textarea>
            <input type='number' onChange={(e) => setReating(e.target.value)} />
            <button onSubmit={handleSubmit}>post</button>
        </form>
    </div>
  )
}

export default Postform
