import React, { useState } from 'react'
//import axios from 'axios'

function Postform({ id }) {
    const [name, setName] = useState('');
    const [reating, setReating] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await fetch('http://localhost:8000/post/64588d3cb79f7d0033b4f0fd',{
                method: 'POST',
                headers: {
                    "x-access-token": localStorage.getItem("token") ,
                },
                body: JSON.stringify({
                    name,
                    reating
                })
            })
        } catch (err) {
            console.log(err)
        }
        
    }

    

  return (
    <div>
        <form>
            <textarea placeholder='write our comment....' onChange={(e) => setName(e.target.value)}></textarea>
            <input type='number' onChange={(e) => setReating(e.target.value)} />
            <button onSubmit={handleSubmit}>post</button>
        </form>
    </div>
  )
}

export default Postform
