import React, { useState } from 'react'
import ReatingCard from './reating/ReatingCard';



function Postform({ id }) {
  const [name, setName] = useState('');
  const [reating, setReating] = useState('');

  const handleSubmit = async (e) => {

    const data = {
      name: name,
      reating: reating
    };

    const url = `http://localhost:8000/post/${id}`;

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      console.log(response);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type='text' placeholder='write our comment....' onChange={(e) => setName(e.target.value)} name='name' />
        <input type='number' onChange={(e) => setReating(e.target.value)} />
        <button type='submit'>post</button>
      </form>
    </div>
  );
}

export default Postform;
