import React, { useEffect, useState } from 'react'
import './moviecard.css'

import movie1 from '../assets/movies/movie1.jpg'
import movie2 from '../assets/movies/movie2.jpg'
import movie3 from '../assets/movies/movie3.jpg'
import movie4 from '../assets/movies/movie4.jpg'
import movie6 from '../assets/movies/movie6.jpg'
import { Link } from 'react-router-dom'


function MoviesCard() {
  const [movies, setMovies] = useState('')

  const fetchdata = async () => {
    const url = 'http://localhost:8000/admin/getmovie'  
    const response = await fetch(url)
    .then((response) => response.json())
    .then((data) => setMovies(data.movies))
  }

  useEffect(() => {
    fetchdata()
  }, [])

  return ( 
    <div className='grid-movie'>
      {Array.isArray(movies)
        ? movies.map(element => {
            return (
              <div>
                <Link to={id}></Link>
                <div className='box'>
                  <img src={`http://localhost:8000/${element.photo}`} alt='movie' />
                  <h3>Name: {element.name}</h3>
                </div>
              </div>
            )
          })
        : null
        }
    </div>

  )
}

export default MoviesCard
