import React, { useState } from 'react'

function Postform({ id }) {
    const [name, setName] = useState('');
    const [reating, setReating] = useState('');
    console.log(id)

    const handleSubmit = async () => {
        await fetch(`http://localhost:8000/post/${id}`, {
          method: 'POST',
          headers: {
            "Content-Type": "application/json",
           },
          body: JSON.stringify({
            name,
            reating
          }),
        });
    }

  return (
    <div>
        <form onSubmit={handleSubmit}>
            <textarea placeholder='write our comment....' onChange={(e) => setName(e.target.value)}></textarea>
            <input type='number' onChange={(e) => setReating(e.target.value)} />
            <button type='submit'>post</button>
        </form>
    </div>
  )
}

export default Postform
